mapper.select:
SELECT B.* FROM(
	SELECT rownum rn, A.* FROM(
		SELECT * FROM posts

			WHERE title LIKE '%'||#{search}||'%' or writer LIKE '%'||#{search}||'%'
			WHERE title LIKE '%'||#{search}||'%'
			WHERE writer LIKE '%'||#{search}||'%'

		 ORDER BY boardId DESC
	)A
)B WHERE rn BETWEEN #{b} and #{e}
